css:
@font-face {
    font-family: myTimes;
    src: url('/files/times.woff');
}

.blackborder {
   border: 1px solid black;
}
.blackborder-left {
  border-top: 1px solid black;
  border-left: 1px solid black;
  border-bottom: 1px solid black;
}
.blackborder-right {
  border-top: 1px solid black;
  border-right: 1px solid black;
  border-bottom: 1px solid black;
}
.blackborder-middle {
  border-top: 1px solid black;
  border-bottom: 1px solid black;
}

.print-format {
  font-family: myTimes;
}

.double_underline {
  border-bottom: double 3px #000000;
}
